/**
 * Der LibraryLoader ist dafür zuständig, verschiedene JavaScript-Libraries zu laden.
 * Welche das sind, werden in dem unten angegeheben Array angegeben.
 *
 * ACHTUNG: Dieser Loader existiert nur während der Entwicklung. In der Produktion wird
 * dieses File überschrieben und enthält dann all diejenigen Libraries, welche im pom.xml definiert
 * wurden.
 */
var LibraryLoader = {
    version: '1.0',
    load:function(id, scripts) {
        if (scripts instanceof Array) {
            var scriptElement = document.getElementById(id);
            if (scriptElement != null) {
                var toCut = id + ".min.js";
                var path = scriptElement.src.substring(0, scriptElement.src.length - toCut.length);
                for (var i = 0; i < scripts.length; i++) {
                    LibraryLoader.insert(path + scripts[i]);
                }
            }
        }
    },
    insert: function(libraryName) {
        document.write('<script type="text/javascript" language="JavaScript" src="' + libraryName + '"><\/script>');
    }
};

LibraryLoader.load('telekurs_full',
        new Array(
                'js/jquery/jquery-1.5.2.min.js',
                'js/jquery/jquery-ui-1.8.4.custom.min.js',
                'js/jquery/jquery.cookie.js',
                'js/jquery/jquery.fieldenhancer.js',
                'js/jquery/jquery.dropdown.js',
                'js/jquery/jquery.toggler.js',
                'js/jquery/jquery.contextmenu.js',
                'js/jquery/jquery.pull.js',
                'js/jquery/jquery.multiselectrow.js',
                'js/jquery/jquery.bgiframe.min.js',
                'js/jquery/jquery.editable-select.pack.js',
                'js/telekurs.js',
                'js/telekurs_util.js',
                'js/shortcuts.js',
                'js/general.js',
                'js/actions.js'
                )
        );
